UPDATE BANNER_TBL
SET 	bannerPath = #{bannerPath}, 
		bannerFileName = #{bannerFileName}, bannerRename=#{bannerRename},
		bannerMsg = #{bannerMsg}, bannerLink = #{bannerLink}
WHERE bannerNo = #{bannerNo}

UPDATE BANNER_TBL
SET 	bannerMsg = #{bannerMsg}, bannerLink = #{bannerLink},
		writer = #{writer}
WHERE 	bannerNo = #{bannerNo}

UPDATE BANNER_TBL
