mapper.select:
SELECT * FROM CHAT_TBL
WHERE refRoomNo = #{roomNo}
ORDER BY 1

SELECT COUNT(*) FROM CHAT_TBL
WHERE refRoomNo = #{refRoomNo}
	AND sender != #{memberNick}
	AND read_chk = 'N'

mapper.update:
UPDATE CHAT_TBL
SET read_chk = 'Y'
WHERE refRoomNo = #{refRoomNo} 
	AND sender != #{memberNick}
