mapper.insert:

	INSERT INTO PARTNER_TBL VALUES(
	SEQ_PARTNERNO.NEXTVAL
	,#{memberNick},#{memberEmail},#{area},#{instrument}
	,#{title},#{contents}
	,#{profileFileName},#{profileRename},#{profilePath}
	,DEFAULT, NULL, DEFAULT
	)

select.if:
SELECT COUNT(*) FROM PARTNER_TBL
title LIKE '%'||#{searchValue}||'%' AND area =#{searchArea}
contents LIKE '%'||#{searchValue}||'%' AND area=#{searchArea}
instrument LIKE '%'||#{searchValue}||'%' AND area=#{searchArea}
(title LIKE '%'||#{searchValue}||'%' AND area =#{searchArea}) OR (contents LIKE '%'||#{searchValue}||'%' AND area=#{searchArea}) OR (instrument LIKE '%'||#{searchValue}||'%' AND area=#{searchArea})
title LIKE '%'||#{searchValue}||'%'
contents LIKE '%'||#{searchValue}||'%'
instrument LIKE '%'||#{searchValue}||'%'
(title LIKE '%'||#{searchValue}||'%') OR (contents LIKE '%'||#{searchValue}||'%') OR (instrument LIKE '%'||#{searchValue}||'%')
AND approval = 'Y'

SELECT * FROM PARTNER_TBL
title LIKE '%'||#{searchValue}||'%'
title LIKE '%'||#{searchValue}||'%' AND area=#{searchArea}
contents LIKE '%'||#{searchValue}||'%'
contents LIKE '%'||#{searchValue}||'%' AND area=#{searchArea}
instrument LIKE '%'||#{searchValue}||'%'
instrument LIKE '%'||#{searchValue}||'%' AND area=#{searchArea}
(title LIKE '%'||#{searchValue}||'%' OR contents LIKE '%'||#{searchValue}||'%' OR instrument LIKE '%'||#{searchValue}||'%')
(title LIKE '%'||#{searchValue}||'%' OR contents LIKE '%'||#{searchValue}||'%' OR instrument LIKE '%'||#{searchValue}||'%') AND area=#{searchArea}
AND approval = 'Y'
ORDER BY 1 DESC

mapper.update:
UPDATE PARTNER_TBL
SET		approval = 'Y', approvalDate = SYSDATE
WHERE partnerNo = #{partnerNo}

UPDATE PARTNER_TBL
SET rejectMailChk = 'Y'
WHERE partnerNo = #{partnerNo}
